<?php

class Solution {

    /**
     * @param String $s
     * @param String[] $words
     * @return Integer[]
     */
    function findSubstring($s, $words) {

        $wordlen = strlen($words[0]);
        $wordcnt = count($words);
        $w = array_count_values($words);
        $slen = strlen($s);
        $wordtotal = $wordlen * $wordcnt;

        $r = [];
        for ($i=0; $i<$slen-$wordtotal+1; $i++) {
            
            $wcurr = $w;
            $cnt = 0;

            $curr = substr($s, $i, $wordlen);
            echo "i=$i curr=$curr wcurr=".join('', $wcurr)."\n";
            if (!isset($w[$curr])) continue;
            $wcurr[$curr]--;
            $cnt++;
            echo join('', $wcurr)."\n";

            for ($j=$i+$wordlen; $j<=$i+$wordtotal-$wordlen; $j+=$wordlen) {
                $curr = substr($s, $j, $wordlen);
                //echo "j=$j curr=$curr wcurr=".join('', $wcurr)."\n";
                if (!isset($wcurr[$curr]) or $wcurr[$curr]==0) break;
                $wcurr[$curr]--;
                $cnt++;
                //echo join('', $wcurr)." cnt=$cnt\n";
            }

            if ($cnt==$wordcnt) $r[] = $i;

        }

        return $r;
    }
}

