<?php

class Solution {

    /**
     * @param String $s
     * @return Integer
     */
    function longestValidParentheses($s) {

        $s = ltrim($s, ')');
        $s = rtrim($s, '(');

        $len = strlen($s);
        $cur = 0;
        $depth = 0;
        $max = 0;
        $j = 0;

        //while ($j<$len) {
            for ($i=0; $i<$len; $i++) {
                //echo "{$s[$i]} cur=$cur depth=$depth max=$max\n";
                if ($s[$i]=='(') {
                    $cur++;
                    $depth++;
                } else {
                    $cur++;
                    $depth--;
                    if ($depth==0) {
                        if ($cur>$max) $max = $cur;
                    } elseif ($depth<0) {
                        $cur = 0;
                        $depth = 0;
                        continue;
                    }
                }
            }
        //    $j++;
        //    $cur = 0;
        //    $depth = 0;            
        //}

        $cur = 0;
        $depth = 0;
        
        for ($i=$len-1; $i>=0; $i--) {
            //echo "{$s[$i]} cur=$cur depth=$depth max=$max\n";
            if ($s[$i]==')') {
                $cur++;
                $depth++;
            } else {
                $cur++;
                $depth--;
                if ($depth==0) {
                    if ($cur>$max) $max = $cur;
                } elseif ($depth<0) {
                    $cur = 0;
                    $depth = 0;
                    continue;
                }
            }
        }

        return $max;
    }
}

