<?php

class Solution {

    public $s;

    function is($left, $right) {

        //echo "$left $right\n";

        if ($this->s[$left]!='(' or $this->s[$right]!=')') return false;

        //echo "1\n";

        $len = $right - $left + 1;
        if ($len % 2 != 0) return false;
        //echo "2\n";

        $va = count_chars(substr($this->s, $left, $len));
        if ($va[40]!=$va[41]) return false;

        $depth = 0;
        for ($i=$left; $i<=$right; $i++) {
            //echo "$i {$this->s[$i]} depth=$depth\n";
            if ($this->s[$i]=='(') {
                $depth++;
            } else {
                $depth--;
                if ($depth<0) {
                    return false;
                }
            }
        }

        //echo $len."\n";

        if ($depth!=0) return false;
        return $len;
    }

    /**
     * @param String $s
     * @return Integer
     */
    function longestValidParentheses($s) {
        $len = strlen($s);
        $left = 0;
        $right = strlen($s) - 1;
        $max = 0;
        $this->s = $s;
        while ($left<$len) {
            for ($right=$len-1; $right>$left; $right--) {
                $ma = $this->is($left, $right);
                if ($ma) {
                    return $ma;
                }
            }
            $left++;
        }
    
        return $max;
    }
}

